INSERT INTO question (question, explanation, idQuiz) VALUES ("Quelle fut la première capitale de l'empire espagnol ?", "De 1492 à 1561, la capitale de l'empire espagnol n'était pas Madrid mais Tolède, située proche de Madrid au centre de l'Espagne.", '42');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quelles étaient les deux couleurs du drapeau espagnol de l'empire ?", "Le drapeau de l'empire espagnol représentait une croix rouge dans un fond blanc.", '42');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Les vikings étaient...", "Les vikings étaient 'païens', c'est un terme créé par les chrétiens pour désigner une personne ni chrétienne ni juive.", '43');
