INSERT INTO question (question, explanation, idQuiz) VALUES ("Dans quel pays les vikings ne vivaient pas ?", "Les vikings vivaient au nord de l'Europe, en Suède, Norvège, Danemark, dans les îles britanniques et en Normandie.", '43');
INSERT INTO question (question, explanation, idQuiz) VALUES ("En quelle année ont eu lieu les premiers raids vikings ?", "D'après une chronique anglo-saxonne, les vikings ont commencé leurs premiers raids en 787.", '43');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien de saisons connaissaient les vikings ?", "Proches du cercle Arctique, les vikings ne connaissaient que 2 saisons, été et hiver et pas de mi-saison.", '43');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Les vikings deviennent des hommes à 12 ans seulement.", "", '43');
