INSERT INTO question (question, explanation, idQuiz) VALUES ("Lequel de ces pays n'a pas colonisé l'Ile Maurice ?", "L'île Maurice a été française, hollandaise et anglaise en dernier avant de prendre son indépendance en 1967.", '44');
