INSERT INTO question (question, explanation, idQuiz) VALUES ("La doctrine Truman est la base de la politique américaine contre l'URSS, elle a pour but de donner une aide financière aux pays européens pour combattre le communisme.", "Elle a été introduite par le président Truman.", '45');
