INSERT INTO question (question, explanation, idQuiz) VALUES ("La Seconde Guerre mondiale a été le conflit le plus meurtrier de l'histoire.", "C'est vrai, avec près de 60 millions de morts dans le monde.", '47');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Les Etats-Unis abandonnent leur neutralité après l'attaque de Pearl Harbor par l'Allemagne.", "Pearl Harbor n'a pas été attaqué par l'Allemagne mais par le Japon.", '47');
