INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui était le Président de la Répoublique française pendant la Seconde Guerre mondiale ?", "C'était De Gaulle au pouvoir, avec son célèbre appel à la résistance le 18 juin 1940.", '47');
