INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien de civils ont été tués par les nazis et leurs alliés ?", "", '47');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Pendant la guerre, les pertes matérielles russes s'élèvent à plus de 600 millions d'euros.", "1710 villes russes ont été détruites, et 70 000 villages.", '47');
