INSERT INTO question (question, explanation, idQuiz) VALUES ("En quelle année a-t-on utiliser pour la première fois l'expression 'révolution industrielle' ?", "C'est l'économiste Adolphe Blanqui qui l'utilise pour la première fois.", '49');
INSERT INTO question (question, explanation, idQuiz) VALUES ("En 1886, Benz met au point la première...", "", '49');
