INSERT INTO question (question, explanation, idQuiz) VALUES ("Quelles sont les siècles correspondants à la période qu'on appelle Renaissance ?", "La Renaissance est une période entre 1300 et 1600.", '50');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Lequel de ces artistes appartient à la Renaissance.", "Monet et Dali n'appartiennent pas à la Renaissance, alors que Raphaël a vécu de 1483 à 1520, en pleine Renaissance.", '50');
INSERT INTO question (question, explanation, idQuiz) VALUES ("On appelle Renaissance autant la période de l'histoire que le mouvement artistique.", "", '50');
