INSERT INTO question (question, explanation, idQuiz) VALUES ("De quel pays est originaire la Renaissance ?", "", '50');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui a peint la chapelle Sixtine ?", "La fresque du plafond de la chapelle Sixtine a été peinte par Michel-Ange entre 1508 et 1512.", '50');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui a peint 'La Cène' ?", "C'est De Vinci qui a peint La Cène de 1495 à 1498.", '50');
