INSERT INTO question (question, explanation, idQuiz) VALUES ("Les Chiefs de Kansas City sont les derniers gagnants du Superbowl ?", "C'est vrai, ils ont déjà été deux fois vainqueurs.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel chanteur a déjà performé au Superbowl ?", "Bruno Mars a effectué une prestation au Superbowl en 2016.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Le record d'audience du Superbowl est de 114,4 millions de téléspectateurs.", "Ce record a eu lieu pour l'édition de 2015.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Sur quelle chaîne française y a-t-il eu le record d'audience du Superbowl en France ?", "En 2019, TF1 a battu le record d'audiance français du Superbowl avec 531 000 téléspectateurs.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel chaîne a eu les droits tv du Superbowl 2020 ?", "", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Laquelle de ces équipes a joué le plus de Superbowl ?", "Ce sont les Patriots avec 11 participations.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qu'est-ce que le Superbowl ?", "Le Superbowl est une finale de championnat qui oppose les vainqueurs des deux conférences.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quelle ville a accueilli le plus de Superbowl ?", "C'est la ville de Miami qui a accueilli le plus de Superbowl, avec 11 fois.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel est le plus grand nombre de points inscrits par l'équipe gagnante du Superbowl ?", "C'est San Francisco qui détient ce record.", '84');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Les Jeux Olympiques d'été datent de...", "Les JO d'été ont été créé en 1896 et ont lieu tous les 4 ans.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("En 2016, à Rio de Janeiro, les Jeux Olympiques d'été ont accueilli...", "On comptait 206 nations et 11 402 athlètes.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui détient le record de médailles et de titres des JO d'été ?", "L'Américain Phelps détient 28 médailles et 23 titres. Il a le record du nombre de médailles et de titres.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel pays possède le plus de médailles en or ?", "Les Etats-Unis possèdent le plus de médailles d'or, avec 2399 médailles, dont 757 en or.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Autrefois, les Jeux Olympiques d'été et d'hiver avaient lieu la même année.", "Ca a été le cas jusqu'en 1994.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien y a-t-il eu d'éditions des Jeux Olympiques d'été ?", "Il y a 28 éditions des JO d'été, et 3 annulées.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien d'épreuves y avait-il aux derniers Jeux Olympiques d'été ?", "", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel pays a le plus de fois accueilli les JO d'été ?", "Ce sont les Etats-Unis, avec 4 fois.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui accueillera les Jeux Olympiques en 2024 ?", "Les Jeux Olympiques d'été de 2024 auront normalement lieu à Paris.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Lequel de ces pays n'a jamais accueilli les JO d'été ?", "L'Espagne a accueilli les JO d'été en 1992, et la Chine en 2008. En revanche, le Danemark ne l'a jamais fait.", '85');
INSERT INTO question (question, explanation, idQuiz) VALUES ("En quelle année a eu lieu la création des Jeux Olympiques d'hiver ?", "Les JO d'hiver ont été créé en 1924 à Chamonix en France.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien y a-t-il eu d'éditions des Jeux Olympiques d'hiver ?", "Il y a eu 23 éditions des JO d'hiver, et 2 annulées.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien y avait-il de femmes lors de la première édition des JO d'hiver ?", "Les premiers Jeux Olympiques d'hiver comptait 11 femmes pour 247 hommes.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien y avait-il d'épreuves lors de la dernière édition des JO d'hiver ?", "La dernière édition des JO d'hiver comptait 102 épreuves dans 7 sports différents.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien y avait-il d'épreuves lors de la première édition des JO d'hiver ?", "Il y avait 16 épreuves dans 6 sports différents.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien de nations ont participé à la dernière édition des Jeux Olympiques d'hiver ?", "", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Qui a organisé les JO d'hiver au moins 3 fois ?", "", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("La Yougoslavie a organisé plus de JO d'hivers que l'Espagne ?", "L'Espagne n'a jamais accueilli les JO d'hiver, contrairement à la Yougoslavie en 1984.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Combien d'épreuves y a-t-il en patinage artistique ?", "Depuis 1924, les Jeux Olympiques d'hiver compte 5 épreuves de patinage artistique.", '86');
INSERT INTO question (question, explanation, idQuiz) VALUES ("Quel est le dernier sport à avoir rejoint les JO d'hiver ?", "C'est le snowboard le dernier à avoir rejoint les JO, en 1998.
